<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\SecurityScopeScopeVO;

trait SecurityScopeScopeAccesor
{
    private SecurityScopeScopeVO $_scope;
    public function getScope(): string
    {
        return $this->_scope->value();
    }
    public function scope(): SecurityScopeScopeVO
    {
        return $this->_scope;
    }
    public function isScopeChanged(?self $original = null): bool
    {
        return $this->_scope->value() !== ($original ? $original->_scope->value() : null);
    }
    public function withScope(SecurityScopeScopeVO|string $scope): self
    {
        $copy = clone $this;
        $copy->_scope = SecurityScopeScopeVO::from($scope);
        return $copy;
    }
}
