use Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\SecurityScopeRelyingPartyVO;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;

trait SecurityScopeRelyingPartyAttributeHolder
{
    protected SecurityScopeRelyingPartyVO|RelyingPartyRef|null $relyingParty = null;
    protected bool $relyingPartyAssigned = false;

    public function getRelyingPartyOrDefault(?SecurityScopeRelyingPartyVO $relyingParty): ?SecurityScopeRelyingPartyVO
    {
        return $this->relyingPartyAssigned ? ($this->relyingParty !== null ? SecurityScopeRelyingPartyVO::from($this->relyingParty) : null) : $relyingParty;
    }
    public function relyingParty(SecurityScopeRelyingPartyVO|RelyingPartyRef|null $relyingParty): static
    {
        $this->relyingParty = $relyingParty;
        $this->relyingPartyAssigned = true;
        return $this;
    }
    public function getRelyingParty(): ?RelyingPartyRef
    {
        return is_a($this->relyingParty, SecurityScopeRelyingPartyVO::class) ? $this->relyingParty->value() : $this->relyingParty;
    }
    public function unsetRelyingParty(): static
    {
        $this->relyingParty = null;
        $this->relyingPartyAssigned = false;
        return $this;
    }
    protected function withDefaultRelyingParty(): void
    {
    }
    protected function withAssertedRelyingPartyRules(self $value, ConstraintFailList $errorsList): void
    {
        if ($value->relyingPartyAssigned = $this->relyingPartyAssigned) {
            $value->relyingParty = SecurityScopeRelyingPartyVO::tryFrom($this->relyingParty, $errorsList);
        }
    }
}
