<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;
use Civi\Lughauth\Shared\Value\Validation\Rule\In;

class SecurityScopeKindVO
{
    public static function empty(): SecurityScopeKindVO
    {
        return new SecurityScopeKindVO(null);
    }
    public static function from(SecurityScopeKindVO|string|null $value): SecurityScopeKindVO
    {
        if (is_a($value, SecurityScopeKindVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            $candidate = self::tryFrom($value, $errorsList);
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $candidate;
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?SecurityScopeKindVO
    {
        if (is_a($value, SecurityScopeKindVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (!$value) {
            return new SecurityScopeKindVO($value);
        } elseif (is_string($value)) {
            // If is a ValueObject, we need to append all the errors for the context
            $valid = true;
            foreach (self::rules() as $rule) {
                if ($fail = $rule->check($value)) {
                    $list->add(ConstraintFail::fromRuleFail('kind', $fail));
                    $valid = false;
                }
            }
            return $valid ? new SecurityScopeKindVO($value) : null;
        } else {
            $list->add(new ConstraintFail('wrong_type', ['kind'], [$value], ['string']));
            return null;
        }
    }
    public static function rules(): array
    {
        return [
          new In(['READ', 'WRITE', 'MANAGE']),
        ];
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?string $kind
    ) {
    }
    public function value(): ?string
    {
        return $this->kind;
    }
}
