<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Infrastructure\Driven;

use Override;
use Throwable;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeReadRepository;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeSlide;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeFilter;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeCursor;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeRef;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScope;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityScopeReadRepositoryAdapter implements SecurityScopeReadRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SecurityScopePdoConnector $conn,
    ) {
    }
    #[Override]
    public function enrich(SecurityScopeRef $ref): ?SecurityScope
    {
        if ($ref instanceof SecurityScope) {
            return $ref;
        } elseif (!$ref->_private_enrich) {
            $ref->_private_enrich = $this->conn->retrieve(new SecurityScopeFilter(uids: [ $ref->uid() ]));
        }
        return $ref->_private_enrich;
    }
    #[Override]
    public function list(?SecurityScopeFilter $filter = null, ?SecurityScopeCursor $sort = null): SecurityScopeSlide
    {
        $this->logDebug("List for Security scope on adapter ");
        $span = $this->startSpan("List for Security scope on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new SecurityScopeSlide(function ($slide, $next) use ($filter) {
                return $this->list($filter, $next);
            }, new SecurityScopeCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieve(?SecurityScopeFilter $filter): ?SecurityScope
    {
        $this->logDebug("Retrieve for Security scope on adapter ");
        $span = $this->startSpan("Retrieve for Security scope on adapter");
        try {
            return $this->conn->retrieve($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function exists(?SecurityScopeFilter $filter): bool
    {
        $this->logDebug("Exists for Security scope on adapter ");
        $span = $this->startSpan("Exists for Security scope on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function count(?SecurityScopeFilter $filter = null): int
    {
        $this->logDebug("Count for Security scope on adapter ");
        $span = $this->startSpan("Count for Security scope on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByUid(string $uid): ?SecurityScope
    {
        $this->logDebug("Find on by uid for Security scope on adapter");
        $span = $this->startSpan("Find on by uid for Security scope on adapter");
        try {
            return $this->conn->retrieve(new SecurityScopeFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByResourceAndScope(string $resource, string $scope): ?SecurityScope
    {
        $this->logDebug("Find on by resource scope for Security scope on adapter");
        $span = $this->startSpan("Find on by resource scope for Security scope on adapter");
        try {
            return $this->conn->retrieve(new SecurityScopeFilter(resourceAndScope: SecurityScopeFilter::resourceAndScopeFilter(resource: $resource, scope: $scope)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
