<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeWriteRepository;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeSlide;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeCursor;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeFilter;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeRef;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScope;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityScopeWriteRepositoryAdapter implements SecurityScopeWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SecurityScopePdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
    ) {
    }
    #[Override]
    public function resolveForUpdate(SecurityScopeRef $ref): ?SecurityScope
    {
        return $this->conn->retrieve(new SecurityScopeFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?SecurityScopeFilter $filter = null, ?SecurityScopeCursor $sort = null): SecurityScopeSlide
    {
        $this->logDebug("Count for Security scope on adapter ");
        $span = $this->startSpan("Count for Security scope on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new SecurityScopeSlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new SecurityScopeCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(SecurityScopeFilter $filter): ?SecurityScope
    {
        $this->logDebug("Count for Security scope on adapter ");
        $span = $this->startSpan("Count for Security scope on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?SecurityScopeFilter $filter): bool
    {
        $this->logDebug("Count for Security scope on adapter ");
        $span = $this->startSpan("Count for Security scope on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?SecurityScopeFilter $filter = null): int
    {
        $this->logDebug("Count for Security scope on adapter ");
        $span = $this->startSpan("Count for Security scope on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(SecurityScope $entity, ?Closure $verify = null): SecurityScope
    {
        $this->logDebug("Count for Security scope on adapter ");
        $span = $this->startSpan("Count for Security scope on adapter");
        try {
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(SecurityScopeRef $reference, SecurityScope $entity): SecurityScope
    {
        $this->logDebug("Count for Security scope on adapter ");
        $span = $this->startSpan("Count for Security scope on adapter");
        try {
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(SecurityScope $entity): bool
    {
        $this->logDebug("Count for Security scope on adapter ");
        $span = $this->startSpan("Count for Security scope on adapter");
        try {
            $result = $this->conn->delete($entity);
            $this->dispach($entity);
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByUid(string $uid): ?SecurityScope
    {
        $this->logDebug("Find on by uid for Security scope on adapter");
        $span = $this->startSpan("Find on by uid for Security scope on adapter");
        try {
            return $this->conn->retrieveForUpdate(new SecurityScopeFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByResourceAndScope(string $resource, string $scope): ?SecurityScope
    {
        $this->logDebug("Find on by resource scope for Security scope on adapter");
        $span = $this->startSpan("Find on by resource scope for Security scope on adapter");
        try {
            return $this->conn->retrieveForUpdate(new SecurityScopeFilter(resourceAndScope: SecurityScopeFilter::resourceAndScopeFilter(resource: $resource, scope: $scope)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(SecurityScope $entity)
    {
        $this->logDebug("Count for Security scope on adapter ");
        $span = $this->startSpan("Count for Security scope on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
