<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Infrastructure\Driver\Batch;

use Override;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeCursor;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskContext;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskCounterInterface;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskReaderInterface;
use Civi\Lughauth\Features\Access\SecurityScope\Application\Usecase\List\SecurityScopeListUsecase;

class SecurityScopeTaskReader implements TaskReaderInterface, TaskCounterInterface
{
    public function __construct(private readonly SecurityScopeListUsecase $usecase)
    {

    }
    #[Override]
    public function read(TaskContext $context): array
    {
        $filter = $context->params['filter'];
        $cursor = $context->cursor;
        $result = $this->usecase->list($filter, $cursor ?? new SecurityScopeCursor(limit: 1));
        $context->cursor = $result->cursor();
        return $result->values();
    }

    #[Override]
    public function expectedTotal(TaskContext $context): int
    {
        $filter = $context->params['filter'];
        return $this->usecase->count($filter);
    }
}
