<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use OpenApi\Attributes as OA;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeFilter;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeCursor;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\SecurityScope\Application\Usecase\List\SecurityScopeListUsecase;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;

class SecurityScopeListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SecurityScopeListUsecase $listUsecase,
        private readonly SecurityScopeRestMapper $mapper,
    ) {
    }
    #[OA\Get(
        path: "/api/access/security-scopes",
        tags: ["Security scope"],
        description:"Create",
        responses: [
        new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/SecurityScopeApiListDTO")),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List Security scope");
        $span = $this->startSpan("List Security scope");
        try {
            $params = $request->getQueryParams();
            $filter = new SecurityScopeFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                resource: $params['resource'] ?? null,
                scope: $params['scope'] ?? null,
                trustedClient: isset($params['trusted-client']) ? new TrustedClientRef($params['trusted-client']) : null,
                trustedClients: isset($params['trusted-clients']) ? explode(",", $params['trusted-clients']) : null,
                relyingParty: isset($params['relying-party']) ? new RelyingPartyRef($params['relying-party']) : null,
                relyingPartys: isset($params['relying-partys']) ? explode(",", $params['relying-partys']) : null,
            );
            $cursor = new SecurityScopeCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapper->mapSecurityScope($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?SecurityScopeCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for Security scope");
        $span = $this->startSpan("Generic allow for Security scope");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
