<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Application\Policy\Allow\Disable;

use Override;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Application\Usecase\Disable\TenantDisableAllowProposal;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class DisableTenantOnlyForRootAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(TenantDisableAllowProposal $proposal): TenantDisableAllowProposal
    {
        $this->logDebug("Check DisableTenantOnlyForRootAllow Tenant");
        $span = $this->startSpan("Check DisableTenantOnlyForRootAllow Tenant");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->allow->allowed) {
                $belongs = $userContext->hasAnyRole('ROOT');
                $allowed = $belongs ? Allow::allowed('disable', 'Enabled for user DisableTenantOnlyForRoot') : Allow::disallowed('disable', 'Disabled if not DisableTenantOnlyForRoot');
                $proposal->allow = $allowed;
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
