<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Application\Policy\Filter;

use Throwable;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Features\Access\Tenant\Application\Service\Visibility\TenantFilterProposal;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantAccesible
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(TenantFilterProposal $event): TenantFilterProposal
    {
        $this->logDebug("Check TenantAccesible Tenant");
        $span = $this->startSpan("Check TenantAccesible Tenant");
        try {
            $userContext = $this->context->getIdentity();
            if (!$userContext->hasAnyRole('ROOT')) {
                if (!$userContext->anonimous && $tenantAccesible = $userContext->tenant) {
                    $event->tenantFilter = $event->tenantFilter->withTenantAccesible($tenantAccesible);
                } else {
                    throw new UnauthorizedException('The claim tenant is required');
                }
            }
            return $event;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
