<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantAttributes;
use Civi\Lughauth\Features\Access\Tenant\Domain\Tenant;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantFilter;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantCursor;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantReadGateway;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantSlide;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantReadGateway $readGateway,
        private readonly TenantWriteGateway $writeGateway
    ) {
    }

    public function prepareVisibleData(Tenant $content): TenantAttributes
    {
        $this->logDebug("Prepare hidratation to visible data for Tenant");
        $span = $this->startSpan("Prepare hidratation to visible data for Tenant");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new TenantExposeProposal($content, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function copyWithFixed(TenantAttributes $attributes, ?Tenant $original = null): TenantAttributes
    {
        $this->logDebug("Copy with fixed for Tenant");
        $span = $this->startSpan("Copy with fixed for  Tenant");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new TenantPresetProposal($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(TenantAttributes $attributes, ?Tenant $original = null): TenantAttributes
    {
        $this->logDebug("Copy with hidden for Tenant");
        $span = $this->startSpan("Copy with hidden for  Tenant");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|TenantRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Tenant");
        $span = $this->startSpan("Check visibility of an iterator for  Tenant");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new TenantFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, TenantRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new TenantRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(TenantFilter $filter, ?TenantCursor $cursor = null): TenantSlide
    {
        $this->logDebug("List visibles for Tenant");
        $span = $this->startSpan("List visibles for  Tenant");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(TenantFilter $filter): int
    {
        $this->logDebug("Count visibles for Tenant");
        $span = $this->startSpan("Count visibles for  Tenant");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(TenantFilter $filter, ?TenantCursor $cursor = null): TenantSlide
    {
        $this->logDebug("List visibles for update for Tenant");
        $span = $this->startSpan("List visibles for update for  Tenant");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(TenantFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Tenant");
        $span = $this->startSpan("Count visibles for update for  Tenant");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(TenantRef $ref): ?Tenant
    {
        $this->logDebug("Retrieve visible for Tenant");
        $span = $this->startSpan("Retrieve visible for  Tenant");
        try {
            $filter = new TenantFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(TenantRef $ref): ?Tenant
    {
        $this->logDebug("Retrieve visible for update for Tenant");
        $span = $this->startSpan("Retrieve visible for update for  Tenant");
        try {
            $filter = new TenantFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?TenantRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Tenant");
        $span = $this->startSpan("Check fields for fix for Tenant");
        try {
            $result = $this->dispacher->dispatch(new TenantFixedFieldsProposal(Tenant::calculatedFields(), $ref));
            return array_merge($this->fieldsToHide($ref), $result->fields);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?TenantRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Tenant");
        $span = $this->startSpan("Check fields to hide for  Tenant");
        try {
            $result = $this->dispacher->dispatch(new TenantHideFieldsProposal([], $ref));
            return $result->fields;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(TenantAttributes $attributes): TenantAttributes
    {
        $this->logDebug("Check visibility of parent references for Tenant");
        $span = $this->startSpan("Check visibility of parent references for  Tenant");
        try {
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(TenantFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Tenant");
        $span = $this->startSpan("Compose visibility filter for  Tenant");
        try {
            $result = $this->dispacher->dispatch(new TenantFilterProposal($filter));
            return $result->tenantFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(Tenant $value): bool
    {
        $this->logDebug("Check if item is visible for Tenant");
        $span = $this->startSpan("Check if item is visible for  Tenant");
        try {
            $result = $this->dispacher->dispatch(new TenantVisibilityProposal(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
