<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Application\Usecase\Delete;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Tenant\Domain\Tenant;
use Civi\Lughauth\Features\Access\Tenant\Application\Service\Visibility\TenantVisibilityService;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantDeleteUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantVisibilityService $visibility,
        private readonly TenantWriteGateway $writer
    ) {
    }

    public function allowDelete(?TenantRef $ref = null): Allow
    {
        $this->logDebug("Check allow of delete usecase for Tenant");
        $span = $this->startSpan("Check allow of delete usecase for Tenant");
        try {
            $result = $this->dispacher->dispatch(new TenantDeleteAllowProposal(Allow::allowed('delete', 'Allowed to delete by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function delete(string $uid): void
    {
        $this->logDebug("Run delete usecase for Tenant");
        $span = $this->startSpan("Run delete usecase for Tenant");
        try {
            $ref = new TenantRef($uid);
            $allow = $this->allowDelete($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new TenantDeleteInputProposal($ref));
            if (!$original = $this->visibility->retrieveVisibleForUpdate($input->ref)) {
                throw new NotFoundException($uid);
            }
            $deleted = $original->delete();
            $this->writer->delete($deleted);
            $this->dispacher->dispatch(new TenantDeleteOuputProposal($ref));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
