<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Application\Usecase\Disable;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantAttributes;
use Civi\Lughauth\Features\Access\Tenant\Application\Service\Visibility\TenantVisibilityService;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantDisableUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantVisibilityService $visibility,
        private readonly TenantWriteGateway $writer
    ) {
    }

    public function allowDisable(?TenantRef $ref = null): Allow
    {
        $this->logDebug("Check allow of Disable usecase for Tenant");
        $span = $this->startSpan("Check allow of Disable usecase for Tenant");
        try {
            $result = $this->dispacher->dispatch(new TenantDisableAllowProposal(Allow::allowed('disable', 'Allowed to Tenant by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function disable(string $uid): TenantAttributes
    {
        $this->logDebug("Run Disable usecase for Tenant");
        $span = $this->startSpan("Run Disable usecase for Tenant");
        try {
            $ref = new TenantRef($uid);
            $allow = $this->allowDisable($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new TenantDisableInputProposal($ref));
            if (!$original = $this->visibility->retrieveVisibleForUpdate($input->ref)) {
                throw new NotFoundException($uid);
            }
            $modified = $original->disable();
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($result->toAttributes());
            return $this->dispacher->dispatch(new TenantDisableOuputProposal($output))->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
