<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\Tenant\Application\Service\Visibility\TenantVisibilityService;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantReadGateway;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantFilter;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantCursor;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantVisibilityService $visibility,
        private readonly TenantReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for Tenant");
        $span = $this->startSpan("Check allow of list usecase for Tenant");
        try {
            $result = $this->dispacher->dispatch(new TenantListAllowDecision(Allow::allowed('list', 'Allowed to list by default')));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(TenantFilter $filter): int
    {
        $this->logDebug("Run list usecase for Tenant");
        $span = $this->startSpan("Run list usecase for Tenant");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            return $this->visibility->countVisibles($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(TenantFilter $filter, ?TenantCursor $cursor = null): TenantAttributesSlide
    {
        $this->logDebug("Run list usecase for Tenant");
        $span = $this->startSpan("Run list usecase for Tenant");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $slide = $this->visibility->listVisibles($filter, $cursor);
            return new TenantAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($item->toAttributes()), $slide->values()));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
