<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Domain\Event;

use Override;
use Civi\Lughauth\Features\Access\Tenant\Domain\Tenant;
use Civi\Lughauth\Shared\Event\PublicEvent;

class TenantDeleteEvent extends TenantEvent implements PublicEvent
{
    public function __construct(
        Tenant $payload
    ) {
        parent::__construct($payload);
    }
    #[Override]
    public function eventType(): string
    {
        return 'tenant.delete';
    }
    #[Override]
    public function schemaVersion(): string
    {
        return 'v1';
    }
    #[Override]
    public function payload(): array
    {
        return [];
    }
    #[Override]
    public function original(): array
    {
        return $this->payload->asPublicJson();
        ;
    }
}
