<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Domain\Gateway;

use Civi\Lughauth\Features\Access\Tenant\Domain\Tenant;

class TenantCursor
{
    public const NAME_ASC = 'name';
    public const NAME_DESC = '-name';
    public const DOMAIN_ASC = 'domain';
    public const DOMAIN_DESC = '-domain';

    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
        private ?string $sinceName = null,
        private ?string $sinceDomain = null,
        private ?array $order = null,
    ) {
    }
    public function next(Tenant $tenant, ?int $size): TenantCursor
    {
        $next = clone $this;
        $next->sinceUid = $tenant->uid();
        $next->sinceName = $tenant->getName();
        $next->sinceDomain = $tenant->getDomain();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
    public function sinceName(): ?string
    {
        return $this->sinceName;
    }
    public function sinceDomain(): ?string
    {
        return $this->sinceDomain;
    }
    public function order(): ?array
    {
        return $this->order;
    }
}
