<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject\TenantDomainVO;

trait TenantDomainAccesor
{
    private TenantDomainVO $_domain;
    public function getDomain(): string
    {
        return $this->_domain->value();
    }
    public function domain(): TenantDomainVO
    {
        return $this->_domain;
    }
    public function isDomainChanged(?self $original = null): bool
    {
        return $this->_domain->value() !== ($original ? $original->_domain->value() : null);
    }
    public function withDomain(TenantDomainVO|string $domain): self
    {
        $copy = clone $this;
        $copy->_domain = TenantDomainVO::from($domain);
        return $copy;
    }
}
