<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject\TenantEnabledVO;

trait TenantEnabledAccesor
{
    private TenantEnabledVO $_enabled;
    public function getEnabled(): bool
    {
        return $this->_enabled->value();
    }
    public function enabled(): TenantEnabledVO
    {
        return $this->_enabled;
    }
    public function isEnabledChanged(?self $original = null): bool
    {
        return $this->_enabled->value() !== ($original ? $original->_enabled->value() : null);
    }
    public function withEnabled(TenantEnabledVO|bool $enabled): self
    {
        $copy = clone $this;
        $copy->_enabled = TenantEnabledVO::from($enabled);
        return $copy;
    }
}
