<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject\TenantRootVO;

trait TenantRootAccesor
{
    private ?TenantRootVO $_root = null;
    public function getRoot(): ?bool
    {
        return $this->_root?->value();
    }
    public function root(): ?TenantRootVO
    {
        return $this->_root;
    }
    public function isRootChanged(?self $original = null): bool
    {
        return $this->_root->value() !== ($original ? $original->_root->value() : null);
    }
    public function withRoot(TenantRootVO|bool|null $root): self
    {
        $copy = clone $this;
        $copy->_root = TenantRootVO::from($root);
        return $copy;
    }
}
