<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class TenantRootVO
{
    public static function empty(): TenantRootVO
    {
        return new TenantRootVO(null);
    }
    public static function from(TenantRootVO|bool|null $value): TenantRootVO
    {
        if (is_a($value, TenantRootVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            return self::tryFrom($value, $errorsList);
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?TenantRootVO
    {
        if (is_a($value, TenantRootVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (!$value) {
            return new TenantRootVO($value);
        } elseif (is_bool($value)) {
            return new TenantRootVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['root'], [$value], ['bool']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?bool $root
    ) {
    }
    public function value(): ?bool
    {
        return $this->root;
    }
}
