                            if ($sortSinceName) {
                                $query .= " and " . ($equals ? substr($equals, 4) . ' and ' : '') . ' "name" > :sinceName';
                                $equals .= ' and "name" = :sinceName';
                                $params[] = new SqlParam(name: 'sinceName', value: $sortSinceName, type: SqlParam::STR);
                            }
                            $order .= ', "access_tenant"."name" asc';
                        }
                        if ($ord === 'nameDesc') {
