<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantWriteRepository;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantSlide;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantCursor;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantFilter;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Tenant\Domain\Tenant;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantWriteRepositoryAdapter implements TenantWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantPdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
    ) {
    }
    #[Override]
    public function resolveForUpdate(TenantRef $ref): ?Tenant
    {
        return $this->conn->retrieve(new TenantFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?TenantFilter $filter = null, ?TenantCursor $sort = null): TenantSlide
    {
        $this->logDebug("Count for Tenant on adapter ");
        $span = $this->startSpan("Count for Tenant on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new TenantSlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new TenantCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(TenantFilter $filter): ?Tenant
    {
        $this->logDebug("Count for Tenant on adapter ");
        $span = $this->startSpan("Count for Tenant on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?TenantFilter $filter): bool
    {
        $this->logDebug("Count for Tenant on adapter ");
        $span = $this->startSpan("Count for Tenant on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?TenantFilter $filter = null): int
    {
        $this->logDebug("Count for Tenant on adapter ");
        $span = $this->startSpan("Count for Tenant on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(Tenant $entity, ?Closure $verify = null): Tenant
    {
        $this->logDebug("Count for Tenant on adapter ");
        $span = $this->startSpan("Count for Tenant on adapter");
        try {
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(TenantRef $reference, Tenant $entity): Tenant
    {
        $this->logDebug("Count for Tenant on adapter ");
        $span = $this->startSpan("Count for Tenant on adapter");
        try {
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(Tenant $entity): bool
    {
        $this->logDebug("Count for Tenant on adapter ");
        $span = $this->startSpan("Count for Tenant on adapter");
        try {
            $result = $this->conn->delete($entity);
            $this->dispach($entity);
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByUid(string $uid): ?Tenant
    {
        $this->logDebug("Find on by uid for Tenant on adapter");
        $span = $this->startSpan("Find on by uid for Tenant on adapter");
        try {
            return $this->conn->retrieveForUpdate(new TenantFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByName(string $name): ?Tenant
    {
        $this->logDebug("Find on by name for Tenant on adapter");
        $span = $this->startSpan("Find on by name for Tenant on adapter");
        try {
            return $this->conn->retrieveForUpdate(new TenantFilter(name: $name));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByDomain(string $domain): ?Tenant
    {
        $this->logDebug("Find on by domain for Tenant on adapter");
        $span = $this->startSpan("Find on by domain for Tenant on adapter");
        try {
            return $this->conn->retrieveForUpdate(new TenantFilter(domain: $domain));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(Tenant $entity)
    {
        $this->logDebug("Count for Tenant on adapter ");
        $span = $this->startSpan("Count for Tenant on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
