<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Tenant\Application\Usecase\Create\TenantCreateUsecase;
use Civi\Lughauth\Features\Access\Tenant\Application\Usecase\List\TenantListUsecase;
use Civi\Lughauth\Features\Access\Tenant\Application\Usecase\Update\TenantUpdateUsecase;
use Civi\Lughauth\Features\Access\Tenant\Application\Usecase\Delete\TenantDeleteUsecase;
use Civi\Lughauth\Features\Access\Tenant\Application\Usecase\Retrieve\TenantRetrieveUsecase;
use Civi\Lughauth\Features\Access\Tenant\Application\Usecase\Enable\TenantEnableUsecase;
use Civi\Lughauth\Features\Access\Tenant\Application\Usecase\Disable\TenantDisableUsecase;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantAllowController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantCreateUsecase $createUsecase,
        private readonly TenantListUsecase $listUsecase,
        private readonly TenantUpdateUsecase $updateUsecase,
        private readonly TenantDeleteUsecase $deleteUsecase,
        private readonly TenantRetrieveUsecase $retrieveUsecase,
        private readonly TenantEnableUsecase $enableUsecase,
        private readonly TenantDisableUsecase $disableUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/me/acl/access/tenants",
        tags: ["Tenant access control"],
        description:"Allows",
        responses: [
        new OA\Response(response: 200, description: "Ok"),
    ]
    )
    ]
    public function genericAllow(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check generic allow for Tenant");
        $span = $this->startSpan("Generic allow for Tenant");
        try {
            $value = [
              'allows' => [
                $this->createUsecase->allowCreate(),
                $this->listUsecase->allowList(),
                $this->updateUsecase->allowUpdate(),
              $this->deleteUsecase->allowDelete(),
              $this->retrieveUsecase->allowRetrieve(),
              $this->enableUsecase->allowEnable(),
              $this->disableUsecase->allowDisable(),
              ],
              'fields' => [
                'noEditables' => $this->updateUsecase->fixedFields(),
                'noVisibles' => $this->retrieveUsecase->hiddenFields()
              ]
            ];
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[OA\Get(
        path: "/api/me/acl/access/tenants/{uid}",
        tags: ["Tenant access control"],
        description:"Allows",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        responses: [
        new OA\Response(response: 200, description: "Ok"),
        new OA\Response(response: 404, description: "Not found"),
    ]
    )
    ]
    public function contextualAllow(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check specific allow for Tenant");
        $span = $this->startSpan("Specific allow for Tenant");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $ref = new TenantRef(uid: $args['uid']);
            $value = [
              'allows' => [
                $this->updateUsecase->allowUpdate($ref),
              $this->deleteUsecase->allowDelete($ref),
              $this->retrieveUsecase->allowRetrieve($ref),
              $this->enableUsecase->allowEnable($ref),
              $this->disableUsecase->allowDisable($ref),
              ],
              'fields' => [
                'noEditables' => $this->updateUsecase->fixedFields($ref),
                'noVisibles' => $this->retrieveUsecase->hiddenFields($ref)
              ]
            ];
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
