<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use OpenApi\Attributes as OA;
use DateTime;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantFilter;
use Civi\Lughauth\Features\Access\Tenant\Domain\Gateway\TenantCursor;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\Tenant\Application\Usecase\List\TenantListUsecase;

class TenantListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantListUsecase $listUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/access/tenants",
        tags: ["Tenant"],
        description:"Create",
        responses: [
        new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TenantApiListDTO")),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List Tenant");
        $span = $this->startSpan("List Tenant");
        try {
            $params = $request->getQueryParams();
            $filter = new TenantFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                name: $params['name'] ?? null,
                domain: $params['domain'] ?? null,
            );
            $cursor = new TenantCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
                sinceName: $params['since-name'] ?? null,
                sinceDomain: $params['since-domain'] ?? null,
                order: isset($params['order']) ? $this->extractOrder(explode(',', $params['order'])) : null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapTenant($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function extractOrder(array $order): array
    {
        $this->logDebug("Extract order for Tenant");
        $span = $this->startSpan("Extract order for Tenant");
        try {
            $mapped = [];
            foreach ($order as $ord) {
                if ($ord === 'name-asc') {
                    $mapped[] = 'nameAsc';
                }
                if ($ord === 'name-desc') {
                    $mapped[] = 'nameDesc';
                }
                if ($ord === 'domain-asc') {
                    $mapped[] = 'domainAsc';
                }
                if ($ord === 'domain-desc') {
                    $mapped[] = 'domainDesc';
                }
            }
            return $mapped;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?TenantCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for Tenant");
        $span = $this->startSpan("Generic allow for Tenant");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            if ($value && strpos('NAME-ASC', $params['order'] ?? '') !== false && $value->sinceName()) {
                $link['since-name'] = 'since-name=' . urlencode($value->sinceName());
            }
            if ($value && strpos('NAME-DESC', $params['order'] ?? '') !== false && $value->sinceName()) {
                $link['since-name'] = 'since-name=' . urlencode($value->sinceName());
            }
            if ($value && strpos('DOMAIN-ASC', $params['order'] ?? '') !== false && $value->sinceDomain()) {
                $link['since-domain'] = 'since-domain=' . urlencode($value->sinceDomain());
            }
            if ($value && strpos('DOMAIN-DESC', $params['order'] ?? '') !== false && $value->sinceDomain()) {
                $link['since-domain'] = 'since-domain=' . urlencode($value->sinceDomain());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapTenant(TenantAttributes $value): TenantApiDTO
    {
        $this->logDebug("Map entity to output dto for Tenant");
        $span = $this->startSpan("Map entity to output dto for Tenant");
        try {
            $dto = new TenantApiDTO();
            $dto->uid = $value->getUid();
            $dto->name = $value->getName();
            $dto->root = $value->getRoot();
            $dto->domain = $value->getDomain();
            $dto->enabled = $value->getEnabled();
            $dto->markForDelete = $value->getMarkForDelete();
            $dto->markForDeleteTime = $value->getMarkForDeleteTime()?->format(DateTime::ATOM);
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
