<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Tenant\Infrastructure\Driver\Rest;

use Psr\Http\Message\ServerRequestInterface;
use DateTime;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject\TenantUidVO;
use Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject\TenantNameVO;
use Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject\TenantRootVO;
use Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject\TenantDomainVO;
use Civi\Lughauth\Features\Access\Tenant\Domain\ValueObject\TenantVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantRestMapper
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function readTenant(ServerRequestInterface $request): TenantAttributes
    {
        $this->logDebug("Read entity for Tenant");
        $span = $this->startSpan("Read entity for Tenant");
        try {
            $body = $request->getParsedBody();
            $dto = new TenantApiDTO();
            $dto->uid = $body['uid'] ?? null;
            $dto->name = $body['name'] ?? null;
            $dto->root = $body['root'] ?? null;
            $dto->domain = $body['domain'] ?? null;
            $dto->enabled = $body['enabled'] ?? null;
            $dto->markForDelete = $body['markForDelete'] ?? null;
            $dto->markForDeleteTime = $body['markForDeleteTime'] ?? null;
            $dto->version = $body['version'] ?? null;
            return $this->mapTenantApiDTO($dto);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function mapTenantApiDTO(TenantApiDTO $body): TenantAttributes
    {
        $this->logDebug("Map input dto to entity for Tenant");
        $span = $this->startSpan("Map input dto to entity for Tenant");
        try {
            $errorsList = new ConstraintFailList();
            $value = new TenantAttributes();
            $value->uid(TenantUidVO::tryFrom($body->uid ?? null, $errorsList));
            $value->name(TenantNameVO::tryFrom($body->name ?? null, $errorsList));
            $value->root(TenantRootVO::tryFrom($body->root ?? null, $errorsList));
            $value->domain(TenantDomainVO::tryFrom($body->domain ?? null, $errorsList));
            // enabled is a calculated value, we cant read it
            // markForDelete is a calculated value, we cant read it
            // markForDeleteTime is a calculated value, we cant read it
            $value->version(TenantVersionVO::tryFrom($body->version ?? null, $errorsList));
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $value;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function mapTenant(TenantAttributes $value): TenantApiDTO
    {
        $this->logDebug("Map entity to output dto for Tenant");
        $span = $this->startSpan("Map entity to output dto for Tenant");
        try {
            $dto = new TenantApiDTO();
            $dto->uid = $value->getUid();
            $dto->name = $value->getName();
            $dto->root = $value->getRoot();
            $dto->domain = $value->getDomain();
            $dto->enabled = $value->getEnabled();
            $dto->markForDelete = $value->getMarkForDelete();
            $dto->markForDeleteTime = $value->getMarkForDeleteTime()?->format(DateTime::ATOM);
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
