<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantConfig\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\TenantConfig\Application\Service\Visibility\TenantConfigVisibilityService;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigReadGateway;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigFilter;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigCursor;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantConfigListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantConfigVisibilityService $visibility,
        private readonly TenantConfigReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for Tenant config");
        $span = $this->startSpan("Check allow of list usecase for Tenant config");
        try {
            $result = $this->dispacher->dispatch(new TenantConfigListAllowDecision(Allow::allowed('list', 'Allowed to list by default')));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(TenantConfigFilter $filter): int
    {
        $this->logDebug("Run list usecase for Tenant config");
        $span = $this->startSpan("Run list usecase for Tenant config");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            return $this->visibility->countVisibles($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(TenantConfigFilter $filter, ?TenantConfigCursor $cursor = null): TenantConfigAttributesSlide
    {
        $this->logDebug("Run list usecase for Tenant config");
        $span = $this->startSpan("Run list usecase for Tenant config");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $slide = $this->visibility->listVisibles($filter, $cursor);
            return new TenantConfigAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($item)), $slide->values()));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
