<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantConfig\Application\Usecase\Update;

use RuntimeException;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\TenantConfig;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class TenantConfigUpdateCheck
{
    public function __construct(public readonly TenantConfigUpdateParams $input, public readonly TenantConfig $current)
    {
    }
    public function reject(RuntimeException|ConstraintFail $fail)
    {
        if ($fail instanceof ConstraintFail) {
            throw ConstraintException::ofFail($fail);
        } else {
            throw $fail;
        }
    }
}
