<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantConfig\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\TenantConfigRef;
use Civi\Lughauth\Features\Access\TenantConfig\Application\Service\Visibility\TenantConfigVisibilityService;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantConfigUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantConfigVisibilityService $visibility,
        private readonly TenantConfigWriteGateway $writer,
    ) {
    }

    public function allowUpdate(?TenantConfigRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for Tenant config");
        $span = $this->startSpan("Check allow update usecase for Tenant config");
        try {
            $result = $this->dispacher->dispatch(new TenantConfigUpdateAllowDecision(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?TenantConfigRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for Tenant config");
        $span = $this->startSpan("Check fixed fields for update usecase for Tenant config");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, TenantConfigUpdateParams $params): TenantConfigUpdateResult
    {
        $this->logDebug("Run update usecase for Tenant config");
        $span = $this->startSpan("Run update usecase for Tenant config");
        try {
            $ref = new TenantConfigRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new TenantConfigUpdateCheck($params, $original));
            $enriched = $this->dispacher->dispatch(new TenantConfigUpdateEnrich($params, $original, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $modified = $original->update($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new TenantConfigUpdateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
