<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Closure;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\TenantConfig;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\TenantConfigRef;
use Civi\Lughauth\Features\Access\TenantConfig\Infrastructure\Driven\TenantConfigWriteRepositoryAdapter;

class TenantConfigWriteGateway
{
    private TenantConfigWriteRepository $repository;
    public function __construct(ContainerInterface $container, ?TenantConfigWriteRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(TenantConfigWriteRepositoryAdapter::class);
    }
    public function create(TenantConfig $entity, ?Closure $verify = null): TenantConfig
    {
        return $this->repository->create($entity, $verify);
    }
    public function update(TenantConfigRef $ref, TenantConfig $entity): TenantConfig
    {
        return $this->repository->update($ref, $entity);
    }
    public function delete(TenantConfig $entity): bool
    {
        return $this->repository->delete($entity);
    }
    public function listForUpdate(?TenantConfigFilter $filter = null, ?TenantConfigCursor $cursor = null): TenantConfigSlide
    {
        return $this->repository->listForUpdate($filter, $cursor);
    }
    public function retrieveForUpdate(TenantConfigFilter $filter): ?TenantConfig
    {
        return $this->repository->retrieveForUpdate($filter);
    }
    public function existsForUpdate(?TenantConfigFilter $filter): bool
    {
        return $this->repository->existsForUpdate($filter);
    }
    public function countForUpdate(?TenantConfigFilter $filter = null): int
    {
        return $this->repository->countForUpdate($filter);
    }
    public function findOneForUpdateByUid(string $uid): ?TenantConfig
    {
        return $this->repository->findOneForUpdateByUid($uid);
    }
    public function findOneForUpdateByTenant(TenantRef $tenant): ?TenantConfig
    {
        return $this->repository->findOneForUpdateByTenant($tenant);
    }
}
