<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantConfig\Infrastructure\Driven;

use Override;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigReadRepository;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigSlide;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigFilter;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigCursor;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\TenantConfigRef;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\TenantConfig;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantConfigReadRepositoryAdapter implements TenantConfigReadRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantConfigPdoConnector $conn,
    ) {
    }
    #[Override]
    public function enrich(TenantConfigRef $ref): ?TenantConfig
    {
        if ($ref instanceof TenantConfig) {
            return $ref;
        } elseif (!$ref->_private_enrich) {
            $ref->_private_enrich = $this->conn->retrieve(new TenantConfigFilter(uids: [ $ref->uid() ]));
        }
        return $ref->_private_enrich;
    }
    #[Override]
    public function list(?TenantConfigFilter $filter = null, ?TenantConfigCursor $sort = null): TenantConfigSlide
    {
        $this->logDebug("List for Tenant config on adapter ");
        $span = $this->startSpan("List for Tenant config on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new TenantConfigSlide(function ($slide, $next) use ($filter) {
                return $this->list($filter, $next);
            }, new TenantConfigCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieve(?TenantConfigFilter $filter): ?TenantConfig
    {
        $this->logDebug("Retrieve for Tenant config on adapter ");
        $span = $this->startSpan("Retrieve for Tenant config on adapter");
        try {
            return $this->conn->retrieve($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function exists(?TenantConfigFilter $filter): bool
    {
        $this->logDebug("Exists for Tenant config on adapter ");
        $span = $this->startSpan("Exists for Tenant config on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function count(?TenantConfigFilter $filter = null): int
    {
        $this->logDebug("Count for Tenant config on adapter ");
        $span = $this->startSpan("Count for Tenant config on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByUid(string $uid): ?TenantConfig
    {
        $this->logDebug("Find on by uid for Tenant config on adapter");
        $span = $this->startSpan("Find on by uid for Tenant config on adapter");
        try {
            return $this->conn->retrieve(new TenantConfigFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByTenant(TenantRef $tenant): ?TenantConfig
    {
        $this->logDebug("Find on by tenant for Tenant config on adapter");
        $span = $this->startSpan("Find on by tenant for Tenant config on adapter");
        try {
            return $this->conn->retrieve(new TenantConfigFilter(tenant: $tenant));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
