<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantConfig\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigWriteRepository;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigSlide;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigCursor;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\Gateway\TenantConfigFilter;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\TenantConfigRef;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\TenantConfig;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantConfigWriteRepositoryAdapter implements TenantConfigWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantConfigPdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
    ) {
    }
    #[Override]
    public function resolveForUpdate(TenantConfigRef $ref): ?TenantConfig
    {
        return $this->conn->retrieve(new TenantConfigFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?TenantConfigFilter $filter = null, ?TenantConfigCursor $sort = null): TenantConfigSlide
    {
        $this->logDebug("Count for Tenant config on adapter ");
        $span = $this->startSpan("Count for Tenant config on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new TenantConfigSlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new TenantConfigCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(TenantConfigFilter $filter): ?TenantConfig
    {
        $this->logDebug("Count for Tenant config on adapter ");
        $span = $this->startSpan("Count for Tenant config on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?TenantConfigFilter $filter): bool
    {
        $this->logDebug("Count for Tenant config on adapter ");
        $span = $this->startSpan("Count for Tenant config on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?TenantConfigFilter $filter = null): int
    {
        $this->logDebug("Count for Tenant config on adapter ");
        $span = $this->startSpan("Count for Tenant config on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(TenantConfig $entity, ?Closure $verify = null): TenantConfig
    {
        $this->logDebug("Count for Tenant config on adapter ");
        $span = $this->startSpan("Count for Tenant config on adapter");
        try {
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(TenantConfigRef $reference, TenantConfig $entity): TenantConfig
    {
        $this->logDebug("Count for Tenant config on adapter ");
        $span = $this->startSpan("Count for Tenant config on adapter");
        try {
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(TenantConfig $entity): bool
    {
        $this->logDebug("Count for Tenant config on adapter ");
        $span = $this->startSpan("Count for Tenant config on adapter");
        try {
            $result = $this->conn->delete($entity);
            $this->dispach($entity);
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByUid(string $uid): ?TenantConfig
    {
        $this->logDebug("Find on by uid for Tenant config on adapter");
        $span = $this->startSpan("Find on by uid for Tenant config on adapter");
        try {
            return $this->conn->retrieveForUpdate(new TenantConfigFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByTenant(TenantRef $tenant): ?TenantConfig
    {
        $this->logDebug("Find on by tenant for Tenant config on adapter");
        $span = $this->startSpan("Find on by tenant for Tenant config on adapter");
        try {
            return $this->conn->retrieveForUpdate(new TenantConfigFilter(tenant: $tenant));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(TenantConfig $entity)
    {
        $this->logDebug("Count for Tenant config on adapter ");
        $span = $this->startSpan("Count for Tenant config on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
