<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Policy\Filter;

use Throwable;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Service\Visibility\TenantLoginProviderFilterProposal;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderFilter;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantAccesible
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(TenantLoginProviderFilterProposal $event): TenantLoginProviderFilterProposal
    {
        $this->logDebug("Check TenantAccesible Tenant login provider");
        $span = $this->startSpan("Check TenantAccesible Tenant login provider");
        try {
            $userContext = $this->context->getIdentity();
            if (!$userContext->hasAnyRole('ROOT')) {
                if (!$userContext->anonimous && $tenantTenantAccesible = $userContext->tenant) {
                    $event->tenantLoginProviderFilter = $event->tenantLoginProviderFilter->withTenantTenantAccesible($tenantTenantAccesible);
                } else {
                    throw new UnauthorizedException('The claim tenant is required');
                }
            }
            return $event;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
