<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Policy\Formula;

use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\ValueObject\TenantLoginProviderTenantVO;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\TenantLoginProviderAttributes;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantCalculator
{
    use LoggerAwareTrait;
    use TracerAwareTrait;
    public function __construct(private readonly Context $context)
    {
    }
    public function calculateTenant(?TenantLoginProviderAttributes $original = null): TenantLoginProviderTenantVO
    {
        $this->logDebug("Check TenantCalculator Tenant login provider");
        $span = $this->startSpan("Check TenantCalculator Tenant login provider");
        try {
            $userContext = $this->context->getIdentity();
            if (!$userContext->hasAnyRole('root:admin')) {
                $value = $userContext->tenant;
                return TenantLoginProviderTenantVO::from(new TenantRef($value));
            }
            return null;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
