<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Application\Service\Visibility\TenantVisibilityService;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\TenantLoginProviderRef;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\TenantLoginProviderAttributes;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\TenantLoginProvider;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderFilter;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderCursor;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderReadGateway;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderSlide;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantLoginProviderVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantLoginProviderReadGateway $readGateway,
        private readonly TenantLoginProviderWriteGateway $writeGateway,
        private readonly TenantVisibilityService $tenantVisibilityService
    ) {
    }

    public function prepareVisibleDataInList(TenantLoginProvider $content): TenantLoginProviderAttributes
    {
        return $this->prepareVisibleDataCallback($content, true);
    }
    public function prepareVisibleData(TenantLoginProvider $content): TenantLoginProviderAttributes
    {
        return $this->prepareVisibleDataCallback($content, false);
    }
    public function copyWithFixed(TenantLoginProviderAttributes $attributes, ?TenantLoginProvider $original = null): TenantLoginProviderAttributes
    {
        $this->logDebug("Copy with fixed for Tenant login provider");
        $span = $this->startSpan("Copy with fixed for  Tenant login provider");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new TenantLoginProviderPresetValues($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(TenantLoginProviderAttributes $attributes, ?TenantLoginProvider $original = null): TenantLoginProviderAttributes
    {
        $this->logDebug("Copy with hidden for Tenant login provider");
        $span = $this->startSpan("Copy with hidden for  Tenant login provider");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|TenantLoginProviderRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Tenant login provider");
        $span = $this->startSpan("Check visibility of an iterator for  Tenant login provider");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new TenantLoginProviderFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, TenantLoginProviderRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new TenantLoginProviderRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(TenantLoginProviderFilter $filter, ?TenantLoginProviderCursor $cursor = null): TenantLoginProviderSlide
    {
        $this->logDebug("List visibles for Tenant login provider");
        $span = $this->startSpan("List visibles for  Tenant login provider");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(TenantLoginProviderFilter $filter): int
    {
        $this->logDebug("Count visibles for Tenant login provider");
        $span = $this->startSpan("Count visibles for  Tenant login provider");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(TenantLoginProviderFilter $filter, ?TenantLoginProviderCursor $cursor = null): TenantLoginProviderSlide
    {
        $this->logDebug("List visibles for update for Tenant login provider");
        $span = $this->startSpan("List visibles for update for  Tenant login provider");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(TenantLoginProviderFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Tenant login provider");
        $span = $this->startSpan("Count visibles for update for  Tenant login provider");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(TenantLoginProviderRef $ref): ?TenantLoginProvider
    {
        $this->logDebug("Retrieve visible for Tenant login provider");
        $span = $this->startSpan("Retrieve visible for  Tenant login provider");
        try {
            $filter = new TenantLoginProviderFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(TenantLoginProviderRef $ref): ?TenantLoginProvider
    {
        $this->logDebug("Retrieve visible for update for Tenant login provider");
        $span = $this->startSpan("Retrieve visible for update for  Tenant login provider");
        try {
            $filter = new TenantLoginProviderFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?TenantLoginProviderRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Tenant login provider");
        $span = $this->startSpan("Check fields for fix for Tenant login provider");
        try {
            $result = $this->dispacher->dispatch(new TenantLoginProviderCollectNonEditableFields(TenantLoginProvider::calculatedFields(), $ref));
            return array_merge($this->fieldsToHide($ref), $result->fields);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?TenantLoginProviderRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Tenant login provider");
        $span = $this->startSpan("Check fields to hide for  Tenant login provider");
        try {
            $result = $this->dispacher->dispatch(new TenantLoginProviderCollectNonVisibleFields([], $ref));
            return $result->fields;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(TenantLoginProviderAttributes $attributes): TenantLoginProviderAttributes
    {
        $this->logDebug("Check visibility of parent references for Tenant login provider");
        $span = $this->startSpan("Check visibility of parent references for  Tenant login provider");
        try {
            if ($attributes->getTenant() && !$this->tenantVisibilityService->checkVisibility($attributes->getTenant())) {
                throw new NotFoundException("Unknow Tenant " . $attributes->getTenant());
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(TenantLoginProviderFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Tenant login provider");
        $span = $this->startSpan("Compose visibility filter for  Tenant login provider");
        try {
            $result = $this->dispacher->dispatch(new TenantLoginProviderRestrictFilterToVisibility($filter));
            return $result->tenantLoginProviderFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(TenantLoginProvider $value): bool
    {
        $this->logDebug("Check if item is visible for Tenant login provider");
        $span = $this->startSpan("Check if item is visible for  Tenant login provider");
        try {
            $result = $this->dispacher->dispatch(new TenantLoginProviderVisibilityCheck(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function prepareVisibleDataCallback(TenantLoginProvider $content, bool $inlist): TenantLoginProviderAttributes
    {
        $this->logDebug("Prepare hidratation to visible data for Tenant login provider");
        $span = $this->startSpan("Prepare hidratation to visible data for Tenant login provider");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new TenantLoginProviderEnrichForView($content, $inlist, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
