<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Usecase\Disable;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\TenantLoginProviderRef;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\TenantLoginProviderAttributes;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Service\Visibility\TenantLoginProviderVisibilityService;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantLoginProviderDisableUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantLoginProviderVisibilityService $visibility,
        private readonly TenantLoginProviderWriteGateway $writer
    ) {
    }

    public function allowDisable(?TenantLoginProviderRef $ref = null): Allow
    {
        $this->logDebug("Check allow of Disable usecase for Tenant login provider");
        $span = $this->startSpan("Check allow of Disable usecase for Tenant login provider");
        try {
            $result = $this->dispacher->dispatch(new TenantLoginProviderDisableAllowProposal(Allow::allowed('disable', 'Allowed to Tenant login provider by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function disable(string $uid): TenantLoginProviderAttributes
    {
        $this->logDebug("Run Disable usecase for Tenant login provider");
        $span = $this->startSpan("Run Disable usecase for Tenant login provider");
        try {
            $ref = new TenantLoginProviderRef($uid);
            $allow = $this->allowDisable($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new TenantLoginProviderDisableInputProposal($ref));
            if (!$original = $this->visibility->retrieveVisibleForUpdate($input->ref)) {
                throw new NotFoundException($uid);
            }
            $modified = $original->disable();
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($result->toAttributes());
            return $this->dispacher->dispatch(new TenantLoginProviderDisableOuputProposal($output))->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
