<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Usecase\Retrieve;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Connector\FileStorage\BinaryContent;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\TenantLoginProviderRef;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Service\Visibility\TenantLoginProviderVisibilityService;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderReadGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantLoginProviderRetrieveUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantLoginProviderVisibilityService $visibility,
        private readonly TenantLoginProviderReadGateway $reader
    ) {
    }

    public function allowRetrieve(?TenantLoginProviderRef $ref = null): Allow
    {
        $this->logDebug("Check allow of retrieve usecase for Tenant login provider");
        $span = $this->startSpan("Check allow of retrieve usecase for Tenant login provider");
        try {
            $result = $this->dispacher->dispatch(new TenantLoginProviderRetrieveAllowDecision(Allow::allowed('retrieve', 'Allowed to retrieve by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function hiddenFields(?TenantLoginProviderRef $ref = null): array
    {
        $this->logDebug("Check hidden fields usecase for Tenant login provider");
        $span = $this->startSpan("Check hidden fields usecase for Tenant login provider");
        try {
            return $this->visibility->fieldsToHide($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieve(string $uid): TenantLoginProviderRetrieveResult
    {
        $this->logDebug("Run retrieve usecase for Tenant login provider");
        $span = $this->startSpan("Run retrieve usecase for Tenant login provider");
        try {
            $ref = new TenantLoginProviderRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$result = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new TenantLoginProviderRetrieveResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveMetadata(string $uid): BinaryContent
    {
        $this->logDebug("Run retrieve Metadata file usecase for Tenant login provider");
        $span = $this->startSpan("Run retrieve Metadata file usecase for Tenant login provider");
        try {
            $ref = new TenantLoginProviderRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$visible = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            if (!$url = $visible->getMetadata()) {
                throw new NotFoundException($uid);
            }
            return $this->reader->readMetadata($url);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
