<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\TenantLoginProviderRef;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Service\Visibility\TenantLoginProviderVisibilityService;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantLoginProviderUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantLoginProviderVisibilityService $visibility,
        private readonly TenantLoginProviderWriteGateway $writer
    ) {
    }

    public function allowUpdate(?TenantLoginProviderRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for Tenant login provider");
        $span = $this->startSpan("Check allow update usecase for Tenant login provider");
        try {
            $result = $this->dispacher->dispatch(new TenantLoginProviderUpdateAllowDecision(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?TenantLoginProviderRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for Tenant login provider");
        $span = $this->startSpan("Check fixed fields for update usecase for Tenant login provider");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, TenantLoginProviderUpdateParams $params): TenantLoginProviderUpdateResult
    {
        $this->logDebug("Run update usecase for Tenant login provider");
        $span = $this->startSpan("Run update usecase for Tenant login provider");
        try {
            $ref = new TenantLoginProviderRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new TenantLoginProviderUpdateCheck($params, $original));
            $enriched = $this->dispacher->dispatch(new TenantLoginProviderUpdateEnrich($params, $original, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $modified = $original->replace($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new TenantLoginProviderUpdateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
