<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Usecase\UploadMetadata;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Connector\FileStorage\BinaryContent;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Service\Visibility\TenantLoginProviderVisibilityService;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantLoginProviderUploadMetadataUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantLoginProviderVisibilityService $visibility,
        private readonly TenantLoginProviderWriteGateway $writer
    ) {
    }

    public function allowUpload(): Allow
    {
        $this->logDebug("Check allow of temp upload Metadata usecase for Tenant login provider");
        $span = $this->startSpan("Check allow of temp upload Metadata usecase for Tenant login provider");
        try {
            $result = $this->dispacher->dispatch(new TenantLoginProviderUploadMetadataAllowDecision(Allow::allowed('upload', 'Allowed to upload by default')));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function readTemporal(string $query): BinaryContent
    {
        $this->logDebug("Run retrieve temp upload Metadata usecase for Tenant login provider");
        $span = $this->startSpan("Run retrieve temp upload Metadata usecase for Tenant login provider");
        try {
            $allow = $this->allowUpload();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            return $this->writer->readTemporalMetadata($query);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function temporalStore(BinaryContent $binary): string
    {
        $this->logDebug("Run temp upload Metadata usecase for Tenant login provider");
        $span = $this->startSpan("Run temp upload Metadata usecase for Tenant login provider");
        try {
            $allow = $this->allowUpload();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $this->dispacher->dispatch(new TenantLoginProviderUploadMetadataCheck($binary));
            $input = $this->dispacher->dispatch(new TenantLoginProviderUploadMetadataEnrich($binary, $binary));
            return $this->writer->temporalStoreMetadata($input->getResult());
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
