<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Event;

use Override;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\TenantLoginProvider;
use Civi\Lughauth\Shared\Event\PublicEvent;

class TenantLoginProviderUpdateEvent extends TenantLoginProviderEvent implements PublicEvent
{
    public function __construct(
        TenantLoginProvider $payload,
        public readonly TenantLoginProvider $original
    ) {
        parent::__construct($payload);
    }
    #[Override]
    public function eventType(): string
    {
        return 'tenant-login-provider.update';
    }
    #[Override]
    public function schemaVersion(): string
    {
        return 'v1';
    }
    #[Override]
    public function payload(): array
    {
        return $this->payload->asPublicJson();
    }
    #[Override]
    public function original(): array
    {
        return $this->original->asPublicJson();
    }
}
