<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\ValueObject\TenantLoginProviderNameVO;

trait TenantLoginProviderNameAccesor
{
    private TenantLoginProviderNameVO $_name;
    public function getName(): string
    {
        return $this->_name->value();
    }
    public function name(): TenantLoginProviderNameVO
    {
        return $this->_name;
    }
    public function isNameChanged(?self $original = null): bool
    {
        return $this->_name->value() !== ($original ? $original->_name->value() : null);
    }
    public function withName(TenantLoginProviderNameVO|string $name): self
    {
        $copy = clone $this;
        $copy->_name = TenantLoginProviderNameVO::from($name);
        return $copy;
    }
}
