<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class TenantLoginProviderMetadataVO
{
    private const EXTERNALS = ['http://', 'https://'];

    public static function fromTemporal(string $key): TenantLoginProviderMetadataVO
    {
        return self::from('temp-store://' . $key);
    }
    public static function fromStored(string $key): TenantLoginProviderMetadataVO
    {
        return self::from(self::hasSomePreffix($key, self::EXTERNALS) ? $key : 'store://' . $key);
    }
    public static function tryFromTemporal(string $key, ConstraintFailList $list): TenantLoginProviderMetadataVO
    {
        return self::tryFrom('temp-store://' . $key, $list);
    }
    public static function tryFromStored(string $key, ConstraintFailList $list): TenantLoginProviderMetadataVO
    {
        return self::tryFrom(self::hasSomePreffix($key, self::EXTERNALS) ? $key : 'store://' . $key, $list);
    }
    private static function isTemporal(?string $key): bool
    {
        return !!$key && strpos($key, 'temp-store://') === 0;
    }
    private static function isStored(?string $key): bool
    {
        return !!$key && strpos($key, 'store://') === 0;
    }
    private static function hasSomePreffix(string $key, array $prefixs): bool
    {
        foreach ($prefixs as $prefix) {
            if (strpos($key, $prefix) === 0) {
                return true;
            }
        }
        return false;
    }
    public static function empty(): TenantLoginProviderMetadataVO
    {
        return new TenantLoginProviderMetadataVO(null);
    }
    public static function from(TenantLoginProviderMetadataVO|string|null $value): TenantLoginProviderMetadataVO
    {
        if (is_a($value, TenantLoginProviderMetadataVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            return self::tryFrom($value, $errorsList);
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?TenantLoginProviderMetadataVO
    {
        if (is_a($value, TenantLoginProviderMetadataVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (!$value) {
            return new TenantLoginProviderMetadataVO($value);
        } elseif (is_string($value)) {
            return new TenantLoginProviderMetadataVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['metadata'], [$value], ['string']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?string $metadata
    ) {
        if ($this->metadata) {
            $this->validateKey($this->metadata);
        }
    }
    public function value(): ?string
    {
        if (self::isTemporal($this->metadata)) {
            return substr($this->metadata, 13);
        } elseif (self::isStored($this->metadata)) {
            return substr($this->metadata, 8);
        } else {
            return $this->metadata;
        }
    }
    private function validateKey(string $key)
    {
        if (!self::hasSomePreffix($key, array_merge(['temp-store://', 'store://'], self::EXTERNALS))) {
            throw new \InvalidArgumentException($key . ' is not a valid upload key');
        }
    }
}
