namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Usecase\Create\TenantLoginProviderCreateUsecase;

class TenantLoginProviderCreateController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SqlTemplate $sql,
        private readonly TenantLoginProviderCreateUsecase $createUsecase,
        private readonly TenantLoginProviderRestMapper $mapper,
    ) {
    }
    #[OA\Post(
        path: "/api/access/login-providers",
        tags: ["Tenant login provider"],
        description:"Create",
        requestBody: new OA\RequestBody(content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TenantLoginProviderApiDTO")),
        responses: [
        new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TenantLoginProviderApiDTO")),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function create(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Create Tenant login provider");
        $span = $this->startSpan("Create Tenant login provider");
        $this->sql->begin();
        try {
            $value = $this->mapper->readTenantLoginProvider($request);
            $result = $this->createUsecase->create($value);
            $this->sql->commit();
            $value = $this->mapper->mapTenantLoginProvider($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(201)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }
}
