class TenantLoginProviderDisableController
{
  use LoggerAwareTrait;
  use TracerAwareTrait;

  public function __construct(
    private readonly Runner $runner,
    private readonly SqlTemplate $sql,
    private readonly TenantLoginProviderDisableUsecase $disableUsecase,
    private readonly Context $context,
  ) {
  }
  #[OA\Patch(path: "/api/access/login-providers/{uid}/disable", tags: ["Tenant login provider"], description: "Update the value",
    parameters: [
      new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
    responses: [
      new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TenantLoginProviderApiDTO")),
      new OA\Response(response: 404, description: "Not found"),
    ])
  ]
  public function disable(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("disable for Tenant login provider");
    $span = $this->startSpan("disable for Tenant login provider");
    $this->sql->begin();
    try {
      if (!isset($args['uid'])) {
        throw new NotFoundException('-');
      }
      $uid = $args['uid'];
      $result = $this->disableUsecase->disable($uid);
      $this->sql->commit();
      $value = $this->mapTenantLoginProvider($result);
      $response->getBody()->write(json_encode($value));
      return $response->withStatus(201)
            ->withHeader('Content-Type', 'application/json');
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $this->sql->close();
      $span->end();
    }
  }
  public function disableAllForQuery(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("disable all for query Tenant login provider");
    $span = $this->startSpan("disable all for query Tenant login provider");
    try {
      $params = $request->getQueryParams();
      $filter = new TenantLoginProviderFilter(
        uids: isset( $params['uid'] ) ? [$params['uid']] : ( isset($params['uids']) ? explode(',', $params['uids']) : null),
        search: $params['search'] ?? null,
        name: $params['name'] ?? null,
        tenant: isset($params['tenant'])? new TenantRef($params['tenant']): null,
        tenants: isset($params['tenants'])? explode(",", $params['tenants']): null,
      );
      $res = $this->runner->run(TenantLoginProviderTaskDisable::class, ['filter' => $filter]);
      $response->getBody()->write( json_encode($res) );      return $response;
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
  public function checkDisableAllForQueryStatus(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("Check disable all for query Tenant login provider");
    $span = $this->startSpan("Check disable all for query Tenant login provider");
    try {
      if (!isset($args['uid'])) {
        throw new NotFoundException('-');
      }
      $uid = $args['uid'];
      $info = $this->runner->detail( new TaskKey($uid) );
      if( !$info ) {
        throw new NotFoundException('-');
      }
      $response->getBody()->write(json_encode($info));
      return $response->withStatus(200)
        ->withHeader('Content-Type', 'application/json');
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
}

  private function mapTenantLoginProvider(TenantLoginProviderDisableResult $value): TenantLoginProviderApiDTO
  {
    $this->logDebug("Map entity to output dto for Tenant login provider");
    $span = $this->startSpan("Map entity to output dto for Tenant login provider");
    try {
      $tenant = $value->getTenant();
      $dto = new TenantLoginProviderApiDTO();
      $dto->uid = $value->getUid();
      $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
      $dto->name = $value->getName();
      $dto->source = $value->getSource();
      $dto->disabled = $value->getDisabled();
      $dto->directAccess = $value->getDirectAccess();
      $dto->publicKey = $value->getPublicKey();
      $dto->privateKey = $value->getPrivateKey();
      $dto->certificate = $value->getCertificate();
      if ($value->getMetadata()) {
        $dto->metadata = $this->context->getBaseUrl() . '/api/access/login-providers/' . $value->getUid() . '/metadata';
      }
      $dto->usersEnabledByDefault = $value->getUsersEnabledByDefault();
      $dto->version = $value->getVersion();
      return $dto;
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
