<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantLoginProvider\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use OpenApi\Attributes as OA;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderFilter;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\Gateway\TenantLoginProviderCursor;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Domain\TenantLoginProviderAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\TenantLoginProvider\Application\Usecase\List\TenantLoginProviderListUsecase;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Shared\Context;

class TenantLoginProviderListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantLoginProviderListUsecase $listUsecase,
        private readonly Context $context,
    ) {
    }
    #[OA\Get(
        path: "/api/access/login-providers",
        tags: ["Tenant login provider"],
        description:"Create",
        responses: [
        new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TenantLoginProviderApiListDTO")),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List Tenant login provider");
        $span = $this->startSpan("List Tenant login provider");
        try {
            $params = $request->getQueryParams();
            $filter = new TenantLoginProviderFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                name: $params['name'] ?? null,
                tenant: isset($params['tenant']) ? new TenantRef($params['tenant']) : null,
                tenants: isset($params['tenants']) ? explode(",", $params['tenants']) : null,
            );
            $cursor = new TenantLoginProviderCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
                sinceName: $params['since-name'] ?? null,
                order: isset($params['order']) ? $this->extractOrder(explode(',', $params['order'])) : null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapTenantLoginProvider($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function extractOrder(array $order): array
    {
        $this->logDebug("Extract order for Tenant login provider");
        $span = $this->startSpan("Extract order for Tenant login provider");
        try {
            $mapped = [];
            foreach ($order as $ord) {
                if ($ord === 'name-asc') {
                    $mapped[] = 'nameAsc';
                }
                if ($ord === 'name-desc') {
                    $mapped[] = 'nameDesc';
                }
            }
            return $mapped;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?TenantLoginProviderCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for Tenant login provider");
        $span = $this->startSpan("Generic allow for Tenant login provider");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            if ($value && strpos('NAME-ASC', $params['order'] ?? '') !== false && $value->sinceName()) {
                $link['since-name'] = 'since-name=' . urlencode($value->sinceName());
            }
            if ($value && strpos('NAME-DESC', $params['order'] ?? '') !== false && $value->sinceName()) {
                $link['since-name'] = 'since-name=' . urlencode($value->sinceName());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapTenantLoginProvider(TenantLoginProviderAttributes $value): TenantLoginProviderApiDTO
    {
        $this->logDebug("Map entity to output dto for Tenant login provider");
        $span = $this->startSpan("Map entity to output dto for Tenant login provider");
        try {
            $tenant = $value->getTenant();
            $dto = new TenantLoginProviderApiDTO();
            $dto->uid = $value->getUid();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $dto->name = $value->getName();
            $dto->source = $value->getSource();
            $dto->disabled = $value->getDisabled();
            $dto->directAccess = $value->getDirectAccess();
            $dto->publicKey = $value->getPublicKey();
            $dto->privateKey = $value->getPrivateKey();
            $dto->certificate = $value->getCertificate();
            if ($value->getMetadata()) {
                $dto->metadata = $this->context->getBaseUrl() . '/api/access/login-providers/' . $value->getUid() . '/metadata';
            }
            $dto->usersEnabledByDefault = $value->getUsersEnabledByDefault();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
