<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantPartySecurity\Application\Usecase\Retrieve;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\TenantPartySecurityRef;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Application\Service\Visibility\TenantPartySecurityVisibilityService;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecurityReadGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantPartySecurityRetrieveUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantPartySecurityVisibilityService $visibility,
        private readonly TenantPartySecurityReadGateway $reader
    ) {
    }

    public function allowRetrieve(?TenantPartySecurityRef $ref = null): Allow
    {
        $this->logDebug("Check allow of retrieve usecase for Tenant party security");
        $span = $this->startSpan("Check allow of retrieve usecase for Tenant party security");
        try {
            $result = $this->dispacher->dispatch(new TenantPartySecurityRetrieveAllowDecision(Allow::allowed('retrieve', 'Allowed to retrieve by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function hiddenFields(?TenantPartySecurityRef $ref = null): array
    {
        $this->logDebug("Check hidden fields usecase for Tenant party security");
        $span = $this->startSpan("Check hidden fields usecase for Tenant party security");
        try {
            return $this->visibility->fieldsToHide($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieve(string $uid): TenantPartySecurityRetrieveResult
    {
        $this->logDebug("Run retrieve usecase for Tenant party security");
        $span = $this->startSpan("Run retrieve usecase for Tenant party security");
        try {
            $ref = new TenantPartySecurityRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$result = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new TenantPartySecurityRetrieveResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
