<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantPartySecurity\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\TenantPartySecurityRef;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Application\Service\Visibility\TenantPartySecurityVisibilityService;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecurityWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantPartySecurityUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantPartySecurityVisibilityService $visibility,
        private readonly TenantPartySecurityWriteGateway $writer
    ) {
    }

    public function allowUpdate(?TenantPartySecurityRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for Tenant party security");
        $span = $this->startSpan("Check allow update usecase for Tenant party security");
        try {
            $result = $this->dispacher->dispatch(new TenantPartySecurityUpdateAllowDecision(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?TenantPartySecurityRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for Tenant party security");
        $span = $this->startSpan("Check fixed fields for update usecase for Tenant party security");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, TenantPartySecurityUpdateParams $params): TenantPartySecurityUpdateResult
    {
        $this->logDebug("Run update usecase for Tenant party security");
        $span = $this->startSpan("Run update usecase for Tenant party security");
        try {
            $ref = new TenantPartySecurityRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new TenantPartySecurityUpdateCheck($params, $original));
            $enriched = $this->dispacher->dispatch(new TenantPartySecurityUpdateEnrich($params, $original, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $modified = $original->update($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new TenantPartySecurityUpdateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
