<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway;

use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\TenantPartySecurity;

class TenantPartySecurityCursor
{
    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
    ) {
    }
    public function next(TenantPartySecurity $tenantPartySecurity, ?int $size): TenantPartySecurityCursor
    {
        $next = clone $this;
        $next->sinceUid = $tenantPartySecurity->uid();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
}
