use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\TenantPartySecurity;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Infrastructure\Driven\TenantPartySecurityReadRepositoryAdapter;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\TenantPartySecurityRef;

class TenantPartySecurityReadGateway
{
    private TenantPartySecurityReadRepository $repository;
    public function __construct(ContainerInterface $container, ?TenantPartySecurityReadRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(TenantPartySecurityReadRepositoryAdapter::class);
    }
    public function resolve(TenantPartySecurityRef $ref): ?TenantPartySecurity
    {
        return $this->repository->resolve($ref);
    }
    public function list(?TenantPartySecurityFilter $filter = null, ?TenantPartySecurityCursor $cursor = null): TenantPartySecuritySlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(TenantPartySecurityFilter $filter): ?TenantPartySecurity
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?TenantPartySecurityFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?TenantPartySecurityFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
    public function findOneByUid(string $uid): ?TenantPartySecurity
    {
        return $this->repository->findOneByUid($uid);
    }
    public function findOneByTenantAndRelyingParty(TenantRef $tenant, RelyingPartyRef $relyingParty): ?TenantPartySecurity
    {
        return $this->repository->findOneByTenantAndRelyingParty($tenant, $relyingParty);
    }
}
