<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantPartySecurity\Infrastructure\Driven;

use Override;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Infrastructure\Connector\Pdo\TenantPartySecurityPdoConnector;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecurityReadRepository;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecuritySlide;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecurityFilter;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecurityCursor;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\TenantPartySecurityRef;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\TenantPartySecurity;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantPartySecurityReadRepositoryAdapter implements TenantPartySecurityReadRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantPartySecurityPdoConnector $conn,
    ) {
    }
    #[Override]
    public function resolve(TenantPartySecurityRef $ref): ?TenantPartySecurity
    {
        if ($ref instanceof TenantPartySecurity) {
            return $ref;
        } elseif (!$ref->_private_resolve) {
            $ref->_private_resolve = $this->conn->retrieve(new TenantPartySecurityFilter(uids: [ $ref->uid() ]));
        }
        return $ref->_private_resolve;
    }
    #[Override]
    public function list(?TenantPartySecurityFilter $filter = null, ?TenantPartySecurityCursor $sort = null): TenantPartySecuritySlide
    {
        $this->logDebug("List for Tenant party security on adapter ");
        $span = $this->startSpan("List for Tenant party security on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new TenantPartySecuritySlide(function ($slide, $next) use ($filter) {
                return $this->list($filter, $next);
            }, new TenantPartySecurityCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieve(?TenantPartySecurityFilter $filter): ?TenantPartySecurity
    {
        $this->logDebug("Retrieve for Tenant party security on adapter ");
        $span = $this->startSpan("Retrieve for Tenant party security on adapter");
        try {
            return $this->conn->retrieve($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function exists(?TenantPartySecurityFilter $filter): bool
    {
        $this->logDebug("Exists for Tenant party security on adapter ");
        $span = $this->startSpan("Exists for Tenant party security on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function count(?TenantPartySecurityFilter $filter = null): int
    {
        $this->logDebug("Count for Tenant party security on adapter ");
        $span = $this->startSpan("Count for Tenant party security on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByUid(string $uid): ?TenantPartySecurity
    {
        $this->logDebug("Find on by uid for Tenant party security on adapter");
        $span = $this->startSpan("Find on by uid for Tenant party security on adapter");
        try {
            return $this->conn->retrieve(new TenantPartySecurityFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByTenantAndRelyingParty(TenantRef $tenant, RelyingPartyRef $relyingParty): ?TenantPartySecurity
    {
        $this->logDebug("Find on by tenant relying party for Tenant party security on adapter");
        $span = $this->startSpan("Find on by tenant relying party for Tenant party security on adapter");
        try {
            return $this->conn->retrieve(new TenantPartySecurityFilter(tenantAndRelyingParty: TenantPartySecurityFilter::tenantAndRelyingPartyFilter(tenant: $tenant, relyingParty: $relyingParty)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
