declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantPartySecurity\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecurityWriteRepository;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecuritySlide;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecurityCursor;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecurityFilter;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\TenantPartySecurityRef;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\TenantPartySecurity;
