use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\EntityChangelog\EntityChangelogService;

class TenantPartySecurityWriteRepositoryAdapter implements TenantPartySecurityWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantPartySecurityPdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
        private readonly EntityChangelogService $changelog,
    ) {
    }
    #[Override]
    public function resolveForUpdate(TenantPartySecurityRef $ref): ?TenantPartySecurity
    {
        return $this->conn->retrieveForUpdate(new TenantPartySecurityFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?TenantPartySecurityFilter $filter = null, ?TenantPartySecurityCursor $sort = null): TenantPartySecuritySlide
    {
        $this->logDebug("Count for Tenant party security on adapter ");
        $span = $this->startSpan("Count for Tenant party security on adapter");
        try {
            $values = $this->conn->listForUpdate($filter, $sort);
            $last = end($values);
            return new TenantPartySecuritySlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new TenantPartySecurityCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(TenantPartySecurityFilter $filter): ?TenantPartySecurity
    {
        $this->logDebug("Count for Tenant party security on adapter ");
        $span = $this->startSpan("Count for Tenant party security on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?TenantPartySecurityFilter $filter): bool
    {
        $this->logDebug("Count for Tenant party security on adapter ");
        $span = $this->startSpan("Count for Tenant party security on adapter");
        try {
            return $this->conn->existsForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?TenantPartySecurityFilter $filter = null): int
    {
        $this->logDebug("Count for Tenant party security on adapter ");
        $span = $this->startSpan("Count for Tenant party security on adapter");
        try {
            return $this->conn->countForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(TenantPartySecurity $entity, ?Closure $verify = null): TenantPartySecurity
    {
        $this->logDebug("Count for Tenant party security on adapter ");
        $span = $this->startSpan("Count for Tenant party security on adapter");
        try {
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            $this->changelog->recordChange('tenant-party-security', $entity->uid(), $entity->asPublicJson(), []);
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(TenantPartySecurityRef $reference, TenantPartySecurity $entity): TenantPartySecurity
    {
        $this->logDebug("Count for Tenant party security on adapter ");
        $span = $this->startSpan("Count for Tenant party security on adapter");
        try {
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            $original = ($reference instanceof TenantPartySecurity) ? $reference : $this->conn->retrieve(new TenantPartySecurityFilter(uids: [ $ref->uid() ]));
            $this->changelog->recordChange('tenant-party-security', $entity->uid(), $entity->asPublicJson(), $original->asPublicJson());
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(TenantPartySecurity $entity): bool
    {
        $this->logDebug("Count for Tenant party security on adapter ");
        $span = $this->startSpan("Count for Tenant party security on adapter");
        try {
            $result = $this->conn->delete($entity);
            $this->dispach($entity);
            $this->changelog->recordDeletion('tenant-party-security', $entity->uid(), $entity->asPublicJson());
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByUid(string $uid): ?TenantPartySecurity
    {
        $this->logDebug("Find on by uid for Tenant party security on adapter");
        $span = $this->startSpan("Find on by uid for Tenant party security on adapter");
        try {
            return $this->conn->retrieveForUpdate(new TenantPartySecurityFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByTenantAndRelyingParty(TenantRef $tenant, RelyingPartyRef $relyingParty): ?TenantPartySecurity
    {
        $this->logDebug("Find on by tenant relying party for Tenant party security on adapter");
        $span = $this->startSpan("Find on by tenant relying party for Tenant party security on adapter");
        try {
            return $this->conn->retrieveForUpdate(new TenantPartySecurityFilter(tenantAndRelyingParty: TenantPartySecurityFilter::tenantAndRelyingPartyFilter(tenant: $tenant, relyingParty: $relyingParty)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(TenantPartySecurity $entity)
    {
        $this->logDebug("Count for Tenant party security on adapter ");
        $span = $this->startSpan("Count for Tenant party security on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
